flattened_metrics AS (
  SELECT
    submission_timestamp,
    submission_date,
    client_id,
    os,
    app_version,
    app_build_id,
    channel,
    process,
    metrics.name AS metric,
    bucket_range,
    value.key AS key,
    value.value AS value
  FROM
    grouped_metrics
  CROSS JOIN
    UNNEST(metrics) AS metrics
  CROSS JOIN
    UNNEST(metrics.value) AS value
),
aggregated AS (
  SELECT
    submission_date,
    client_id,
    os,
    app_version,
    app_build_id,
    channel,
    process,
    metric,
    key,
    ARRAY_AGG(bucket_range) AS bucket_range,
    ARRAY_AGG(value) AS value
  FROM
    flattened_metrics
  GROUP BY
    client_id,
    submission_date,
    os,
    app_version,
    app_build_id,
    channel,
    process,
    metric,
    key
)
SELECT
  submission_date,
  client_id,
  os,
  app_version,
  app_build_id,
  channel,
  ARRAY_AGG(
    STRUCT<
      metric STRING,
      metric_type STRING,
      key STRING,
      process STRING,
      agg_type STRING,
      bucket_range STRUCT<first_bucket INT64, last_bucket INT64, num_buckets INT64>,
      value ARRAY<STRUCT<key STRING, value INT64>>
    >(
      metric,
      udf_get_histogram_type(value),
      key,
      process,
      'summed_histogram',
      bucket_range[OFFSET(0)],
      udf_aggregate_json_sum(value)
    )
  ) AS histogram_aggregates
FROM
  aggregated
GROUP BY
  client_id,
  submission_date,
  os,
  app_version,
  app_build_id,
  channel
