aggregated AS (
  SELECT
      submission_date,
      client_id,
      os,
      app_version,
      app_build_id,
      channel,
      process,
      metric,
      key,
      ARRAY_AGG(value) as bucket_range,
      ARRAY_AGG(value) as value
  FROM flattened_metrics
  GROUP BY
      client_id,
      submission_date,
      os,
      app_version,
      app_build_id,
      channel,
      process,
      metric,
      key)

SELECT
  client_id,
  submission_date,
  os,
  app_version,
  app_build_id,
  channel,
  ARRAY_AGG(STRUCT<
    metric STRING,
    metric_type STRING,
    key STRING,
    process STRING,
    agg_type STRING,
    bucket_range STRUCT<
      first_bucket INT64,
      last_bucket INT64,
      num_buckets INT64
    >,
    value ARRAY<STRUCT<key STRING, value INT64>>
  >(
      metric,
      udf_get_histogram_type(bucket_range),
      key,
      process,
      '',
      udf_get_bucket_range(bucket_range),
      udf_aggregate_json_sum(value)
  )) AS histogram_aggregates
FROM aggregated
GROUP BY
  client_id,
  submission_date,
  os,
  app_version,
  app_build_id,
  channel
